const useAxios = (url: string) => {
  const [news, setNews] = useState<INews>();
  const [isError, setIsError] = useState(false);
  const fetchData = async () => {
    try {
      const response = await axios.get(url);
      setNews(response.data);
    } catch (error) {
      setIsError(true);
      console.log(error);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);

  return { isError, news };
};
export default useAxios;
