  //     } catch (error) {
  //       console.log(error);
  //     }
  //   };
  //   fetchData();
  // }, []);
  // console.log(news);
  const { isLoading, isError, news } = useAxios("https://newsapi.org/v2/top-headlines?country=id&apiKey=6ea25a8dca434dd38d9adfef34ecfa02");
  return (
    <>
      <Navbar />
      <Container fluid="md" className="py-4">
        <Row>
          {news ? (
            news.articles.map((article: IArticles) => (
              <Col md="4" sm="12" className="mt-4" key={article.title}>
                <Cards {...article} />
              </Col>
            ))
          ) : (
            <div className="d-flex justify-content-center align-items-center">
              <Spinner>Loading...</Spinner>
            </div>
          )}
        </Row>
      </Container>
    </>
  );
}

export default App;
